import React, { useState } from "react";
import PropTypes from "prop-types";

const setURL = (category) => {
  switch (category) {
    case "all":
      return "s=";
    default:
      return `filter.php?c=${category}`;
  }
};

function CardFilters({ categories, setFoodFilter }) {
  const [categorySel, setCategorySel] = useState("all");
  return (
    <div>
      <button
        type="button"
        value="all"
        data-testid="All-category-filter"
        onClick={({ target: { value } }) => {
          setFoodFilter(setURL(value));
          setCategorySel("all");
        }}
      >
        All
      </button>
      {categories.slice(0, 5).map(({ category }) => (
        <button
          type="button"
          data-testid={`${category}-category-filter`}
          value={category}
          onClick={({ target: { value } }) => {
            if (categorySel === value) {
              setCategorySel("all");
              return setFoodFilter(setURL("all"));
            }
            setCategorySel(value);
            return setFoodFilter(setURL(value));
          }}
        >
          {category}
        </button>
      ))}
    </div>
  );
}

CardFilters.propTypes = {
  categories: PropTypes.instanceOf(Array).isRequired,
};

export default CardFilters;
